import React, { useState, useRef } from "react";
import ForecastCard from './components/ForecastCard';
import GetForecastList from './services/ForecastService';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

export default function Forecast() {
  const [forecastResponse, setforecastResponse] = useState({
    "generatedAt": "2022-03-15T02:33:12-03:00",
    "periods": [
        {
            "startTime": "2022-03-15T02:00:00-03:00",
            "endTime": "2022-03-15T07:00:00-03:00",
            "isDaytime": false,
            "temperature": 40,
            "temperatureUnit": "F",
            "temperatureTrend": null,
            "windSpeed": "7 mph",
            "windDirection": "SW",
            "icon": "https://api.weather.gov/icons/land/night/skc?size=medium",
            "shortForecast": "Clear",
            "detailedForecast": "Clear, with a low around 40. Southwest wind around 7 mph."
        },
        {
            "startTime": "2022-03-15T07:00:00-03:00",
            "endTime": "2022-03-15T19:00:00-03:00",
            "isDaytime": true,
            "temperature": 67,
            "temperatureUnit": "F",
            "temperatureTrend": null,
            "windSpeed": "6 mph",
            "windDirection": "SW",
            "icon": "https://api.weather.gov/icons/land/day/sct?size=medium",
            "shortForecast": "Mostly Sunny",
            "detailedForecast": "Mostly sunny, with a high near 67. Southwest wind around 6 mph."
        }
    ]
});
  const [error, setError] = useState()
  
  const streetRef = useRef()
  const cityRef = useRef()
  const stateRef = useRef()
  const zipcodeRef = useRef()
  

  function handleOnClick(){
    const street = streetRef.current.value;
    const city = cityRef.current.value;
    const state = stateRef.current.value;
    const zipcode = zipcodeRef.current.value;
    console.log(city);

    GetForecastList(street, city, state, zipcode, setforecastResponse, setError)
  }

  return (
    <>
      <div>Forecast</div>
      <TextField label="Street" variant="outlined" ref={streetRef} type="text" />
      <TextField label="City" variant="outlined" ref={cityRef} type="text" />
      <TextField label="State" variant="outlined" ref={stateRef} type="text" />
      <TextField label="Zipcode" variant="outlined" ref={zipcodeRef} type="text" />
      <Button variant="contained" onClick={handleOnClick}>Search</Button>

      {forecastResponse && forecastResponse.periods.map((forecast, index) => {
        return <ForecastCard key={index} forecast={forecast} />;
      })}

    </>
  );
}
